(function($){var uploaders={};function _(str){return plupload.translate(str)||str;}
function renderUI(id,target){target.contents().each(function(i,node){node=$(node);if(!node.is('.plupload')){node.remove();}});target.prepend('<div class="plupload_wrapper plupload_scroll">'+'<div id="'+id+'_container" class="plupload_container">'+'<div class="plupload">'+'<div class="plupload_header">'+'<div class="plupload_header_content">'+'<div class="plupload_header_title">'+_('Select files')+'</div>'+'<div class="plupload_header_text">'+_('Add files to the upload queue and click the start button.')+'</div>'+'</div>'+'</div>'+'<div class="plupload_content">'+'<div class="plupload_filelist_header">'+'<div class="plupload_file_name">'+_('Filename')+'</div>'+'<div class="plupload_file_action">&nbsp;</div>'+'<div class="plupload_file_status"><span>'+_('Status')+'</span></div>'+'<div class="plupload_file_size">'+_('Size')+'</div>'+'<div class="plupload_clearer">&nbsp;</div>'+'</div>'+'<ul id="'+id+'_filelist" class="plupload_filelist"></ul>'+'<div class="plupload_filelist_footer">'+'<div class="plupload_file_name">'+'<div class="plupload_buttons">'+'<a href="#" class="plupload_button plupload_add">'+_('Add files')+'</a>'+'<a href="#" class="plupload_button plupload_start">'+_('Start upload')+'</a>'+'</div>'+'<span class="plupload_upload_status"></span>'+'</div>'+'<div class="plupload_file_action"></div>'+'<div class="plupload_file_status"><span class="plupload_total_status">0%</span></div>'+'<div class="plupload_file_size"><span class="plupload_total_file_size">0 b</span></div>'+'<div class="plupload_progress">'+'<div class="plupload_progress_container">'+'<div class="plupload_progress_bar"></div>'+'</div>'+'</div>'+'<div class="plupload_clearer">&nbsp;</div>'+'</div>'+'</div>'+'</div>'+'</div>'+'<input type="hidden" id="'+id+'_count" name="'+id+'_count" value="0" />'+'</div>');}
$.fn.pluploadQueue=function(settings){if(settings){this.each(function(){var uploader,target,id;target=$(this);id=target.attr('id');if(!id){id=plupload.guid();target.attr('id',id);}
uploader=new plupload.Uploader($.extend({dragdrop:true,container:id},settings));if(settings.preinit){settings.preinit(uploader);}
uploaders[id]=uploader;function handleStatus(file,res){var actionClass;if($('#'+file.id).hasClass('plupload_failed')){return;}
if(file.status==plupload.DONE){if(res){var response=$.parseJSON(res.response);if(response.error){$('#'+file.id).attr('class','plupload_failed').find('a').css('display','block').attr('title',response.error.message);return;}}
actionClass='plupload_done';}
if(file.status==plupload.FAILED){actionClass='plupload_failed';}
if(file.status==plupload.QUEUED){actionClass='plupload_delete';}
if(file.status==plupload.UPLOADING){actionClass='plupload_uploading';}
$('#'+file.id).attr('class',actionClass).find('a').css('display','block');}
function updateTotalProgress(){$('span.plupload_total_status',target).html(uploader.total.percent+'%');$('div.plupload_progress_bar',target).css('width',uploader.total.percent+'%');$('span.plupload_upload_status',target).text('Uploaded '+uploader.total.uploaded+'/'+uploader.files.length+' files');if(uploader.total.uploaded==uploader.files.length){uploader.stop();}}
function updateList(){var fileList=$('ul.plupload_filelist',target).html(''),inputCount=0,inputHTML;$.each(uploader.files,function(i,file){inputHTML='';if(file.status==plupload.DONE){if(file.target_name){inputHTML+='<input type="hidden" name="'+id+'_'+inputCount+'_tmpname" value="'+plupload.xmlEncode(file.target_name)+'" />';}
inputHTML+='<input type="hidden" name="'+id+'_'+inputCount+'_name" value="'+plupload.xmlEncode(file.name)+'" />';inputHTML+='<input type="hidden" name="'+id+'_'+inputCount+'_status" value="'+(file.status==plupload.DONE?'done':'failed')+'" />';inputCount++;$('#'+id+'_count').val(inputCount);}
fileList.append('<li id="'+file.id+'">'+'<div class="plupload_file_name"><span>'+file.name+'</span></div>'+'<div class="plupload_file_action"><a href="#"></a></div>'+'<div class="plupload_file_status">'+file.percent+'%</div>'+'<div class="plupload_file_size">'+plupload.formatSize(file.size)+'</div>'+'<div class="plupload_clearer">&nbsp;</div>'+
inputHTML+'</li>');handleStatus(file);$('#'+file.id+'.plupload_delete a').click(function(e){$('#'+file.id).remove();uploader.removeFile(file);e.preventDefault();});});$('span.plupload_total_file_size',target).html(plupload.formatSize(uploader.total.size));if(uploader.total.queued===0){$('span.plupload_add_text',target).text(_('Add files.'));}else{$('span.plupload_add_text',target).text(uploader.total.queued+' files queued.');}
$('a.plupload_start',target).toggleClass('plupload_disabled',uploader.files.length===0);fileList[0].scrollTop=fileList[0].scrollHeight;updateTotalProgress();if(!uploader.files.length&&uploader.features.dragdrop&&uploader.settings.dragdrop){$('#'+id+'_filelist').append('<li class="plupload_droptext">'+_("Drag files here.")+'</li>');}}
uploader.bind("UploadFile",function(up,file){$('#'+file.id).addClass('plupload_current_file');});uploader.bind('Init',function(up,res){renderUI(id,target);if(!settings.unique_names&&settings.rename){$('#'+id+'_filelist div.plupload_file_name span',target).live('click',function(e){var targetSpan=$(e.target),file,parts,name,ext="";file=up.getFile(targetSpan.parents('li')[0].id);name=file.name;parts=/^(.+)(\.[^.]+)$/.exec(name);if(parts){name=parts[1];ext=parts[2];}
targetSpan.hide().after('<input type="text" />');targetSpan.next().val(name).focus().blur(function(){targetSpan.show().next().remove();}).keydown(function(e){var targetInput=$(this);if(e.keyCode==13){e.preventDefault();file.name=targetInput.val()+ext;targetSpan.text(file.name);targetInput.blur();}});});}
$('a.plupload_add',target).attr('id',id+'_browse');up.settings.browse_button=id+'_browse';if(up.features.dragdrop&&up.settings.dragdrop){up.settings.drop_element=id+'_filelist';$('#'+id+'_filelist').append('<li class="plupload_droptext">'+_("Drag files here.")+'</li>');}
$('#'+id+'_container').attr('title','Using runtime: '+res.runtime);$('a.plupload_start',target).click(function(e){if(!$(this).hasClass('plupload_disabled')){uploader.start();}
e.preventDefault();});$('a.plupload_stop',target).click(function(e){uploader.stop();e.preventDefault();});$('a.plupload_start',target).addClass('plupload_disabled');});uploader.init();if(settings.setup){settings.setup(uploader);}
uploader.bind("Error",function(up,err){var file=err.file,message;if(file){message=err.message;if(err.details){message+=" ("+err.details+")";}
$('#'+file.id).attr('class','plupload_failed').find('a').css('display','block').attr('title',message);}});uploader.bind('StateChanged',function(){if(uploader.state===plupload.STARTED){$('li.plupload_delete a,div.plupload_buttons',target).hide();$('span.plupload_upload_status,div.plupload_progress,a.plupload_stop',target).css('display','block');$('span.plupload_upload_status',target).text('Uploaded 0/'+uploader.files.length+' files');}else{$('a.plupload_stop,div.plupload_progress',target).hide();$('a.plupload_delete',target).css('display','block');}});uploader.bind('QueueChanged',updateList);uploader.bind('StateChanged',function(up){if(up.state==plupload.STOPPED){updateList();}});uploader.bind('FileUploaded',function(up,file,res){handleStatus(file,res);});uploader.bind("UploadProgress",function(up,file,res){$('#'+file.id+' div.plupload_file_status',target).html(file.percent+'%');handleStatus(file,res);updateTotalProgress();});});return this;}else{return uploaders[$(this[0]).attr('id')];}};})(jQuery);